from .models import Country, Profile, Disease, Contagionfrom rest_framework.serializers import ModelSerializerclass CountrySerializer(ModelSerializer):    class Meta:        model = Country        fields = ['id', 'name', 'parent', 'order', 'flag', 'is_active']class ProfileSerializer(ModelSerializer):    class Meta:        model = Profile        fields = [            'id', 'user', 'email', 'photo',            'date_of_birth', 'gender', 'mobile', 'address', 'bio'        ]class DiseaseSerializer(ModelSerializer):    class Meta:        model = Disease        fields = [            'id', 'disease_name', 'disease_image'        ]class ContagionSerializer(ModelSerializer):    class Meta:        model = Contagion        fields = [            'id', 'disease', 'country', 'reporter', 'daily_test',            'daily_effected', 'daily_dies', 'daily_recovery'        ]        read_only_field = ['reporter']