from django.db import modelsfrom django.contrib.auth.models import Userfrom core.models.base import BaseEntityfrom user.models import Rolefrom core.helpers.enums import *from hospital.models.management import *class Patient(BaseEntity):    username = models.OneToOneField(User, on_delete=models.CASCADE, related_name='PatientProfile')    address = models.TimeField()    phone_number = models.IntegerField(default=0)    profile_photo = models.ImageField(upload_to='patient')    age = models.ImageField(0)    city = models.CharField(max_length=120, blank=True, null=True)    postal_code = models.IntegerField(default=0)    date_of_birth = models.DateTimeField(auto_now_add=False)    reference_name = models.CharField(max_length=70, blank=True, null=True)    reference_phone_number = models.ImageField(default=0)    gender = models.CharField(choices=GenderEnum.choices(), default=GenderEnum.MALE.value, max_length=10)    def __str__(self):        return self.username.username