from django.db import modelsfrom core.models.base import BaseEntityfrom hospital.models.management import Departmentfrom hospital.models.schedule import Scheduleclass Appointment(BaseEntity):    name = models.CharField(max_length=90)    Department_name = models.ForeignKey(Department, on_delete=models.SET_NULL, null=True,                                        related_name='doctor_appointment')    schedules = models.ForeignKey(Schedule, on_delete=models.SET_NULL, null=True, related_name='doctor_schedules')    phn_number = models.IntegerField(default=0)    email = models.EmailField(blank=True, null=True)    problem = models.TextField(blank=True, null=True)    address = models.TextField()    def __str__(self):        return self.name