from django.db import modelsfrom django.contrib.auth.models import Userfrom core.models.base import BaseEntityfrom user.models import Rolefrom core.helpers.enums import *from hospital.models.management import *class Doctor(BaseEntity):    username = models.OneToOneField(User, on_delete=models.CASCADE, related_name='DoctorProfile')    specialization = models.CharField(max_length=100)    location = models.CharField(max_length=120)    phone_number = models.IntegerField(default=0)    education = models.TextField()    date_of_birth = models.DateTimeField(auto_now_add=False)    visit_fee = models.IntegerField(default=0)    designation = models.CharField(max_length=70)    roles = models.ManyToManyField(Role)    profile_photo = models.ImageField(upload_to='doctor')    department = models.ForeignKey(Department, on_delete=models.SET_NULL, null=True, related_name='employee_department')    availability = models.ForeignKey(Availability, on_delete=models.SET_NULL, null=True,                                     related_name='doctor_availability')    experience = models.ForeignKey(Experience, on_delete=models.SET_NULL, blank=True, null=True,                                   related_name='doctor_experience')    gender = models.CharField(choices=GenderEnum.choices(), default=GenderEnum.MALE.value, max_length=10)    def __str__(self):        return self.username.username