from django.db import modelsfrom core.models.base import BaseEntityfrom hospital.models.doctor import Doctorclass Schedule(BaseEntity):    doctor_name = models.ForeignKey(Doctor, on_delete=models.SET_NULL, null=True, related_name='doctor_schedule')    available_days = models.CharField(max_length=50)    start_time = models.TimeField(auto_now_add=False)    end_time = models.TimeField(auto_now_add=False)    message = models.TextField()    is_active = models.BooleanField(default=True)    def __str__(self):        return self.doctor_name.username