from .models import *from user.serializers import UserSerializerfrom rest_framework.serializers import ModelSerializer, SerializerMethodFieldclass ExperienceSerializer(ModelSerializer):    class Meta:        model = Experience        fields = (            '__all__'        )class AvailabilitySerializer(ModelSerializer):    class Meta:        model = Availability        fields = (            '__all__'        )class LaboratoriesSerializer(ModelSerializer):    class Meta:        model = Laboratories        fields = (            '__all__'        )class DepartmentSerializer(ModelSerializer):    class Meta:        model = Department        fields = (            '__all__'        )class ServiceSerializer(ModelSerializer):    class Meta:        model = Service        fields = (            '__all__'        )    def to_representation(self, instance):        response = super().to_representation(instance)        response['laboratories'] = LaboratoriesSerializer(instance.laboratories).data        return responseclass DoctorSerializer(ModelSerializer):    roles = SerializerMethodField()    class Meta:        model = Doctor        fields = {            '__all__'        }        read_only_fields = ['roles']