from hospital.models.doctor import Doctorfrom user.serializers.profile_serializer import UserSerializerfrom rest_framework.serializers import ModelSerializerclass DoctorSerializer(ModelSerializer):    class Meta:        model = Doctor        fields = (            'id', 'username', 'specialization', 'location', 'phone_number', 'education',            'date_of_birth', 'visit_fee', 'designation', 'roles', 'profile_photo',            'department', 'availability', 'experience', 'gender', 'created_at', 'updated_at'        )        read_only_fields = ['username']    def to_representation(self, instance):        response = super().to_representation(instance)        response['username'] = UserSerializer(instance.username).data        return response