from hospital.models.doctor import Doctorfrom rest_framework.serializers import ModelSerializer, SerializerMethodFieldclass DoctorSerializer(ModelSerializer):    # roles = SerializerMethodField()    class Meta:        model = Doctor        fields = (            'id', 'username', 'specialization', 'location', 'phone_number', 'education',            'date_of_birth', 'visit_fee', 'designation', 'roles', 'profile_photo',            'department', 'availability', 'experience', 'gender', 'created_at', 'updated_at'        )        read_only_fields = ['roles', 'username']