from hospital.models.management import *from rest_framework.serializers import ModelSerializer, TimeFieldclass ExperienceSerializer(ModelSerializer):    class Meta:        model = Experience        fields = (            '__all__'        )class AvailabilitySerializer(ModelSerializer):    start_time = TimeField(format='%I:%M %p')    end_time = TimeField(format='%I:%M %p')    class Meta:        model = Availability        fields = ('id', 'day', 'start_time', 'end_time')class LaboratoriesSerializer(ModelSerializer):    class Meta:        model = Laboratories        fields = (            '__all__'        )class DepartmentSerializer(ModelSerializer):    class Meta:        model = Department        fields = (            '__all__'        )class ServiceSerializer(ModelSerializer):    class Meta:        model = Service        fields = (            '__all__'        )    def to_representation(self, instance):        response = super().to_representation(instance)        response['laboratories'] = LaboratoriesSerializer(instance.laboratories).data        return response