from hospital.views.management_views import *from hospital.views.doctor_views import DoctorViewSetfrom hospital.views.appointment_views import AppointmentViewSetfrom rest_framework import routersfrom django.urls import path, includerouter = routers.DefaultRouter()router.register('experience', ExperienceViewSet)router.register('availability', AvailabilityViewSet)router.register('laboratories', LaboratoriesViewSet)router.register('department', DepartmentViewSet)router.register('service', ServiceViewSet)router.register('doctor', DoctorViewSet)router.register('appointment', AppointmentViewSet)urlpatterns = [    path('', include(router.urls)),]