from django.db import modelsclass Role(models.Model):    """     The Role entries are managed by the system,    automatically created via a Django data migration.    """    Doctor = 1    Nurse = 2    Laboratories = 3    Accountant = 4    ADMIN = 5    ROLE_CHOICES = (        (Doctor, 'doctor'),        (Nurse, 'nurse'),        (Laboratories, 'laboratories'),        (Accountant, 'accountant'),        (ADMIN, 'admin'),    )    id = models.PositiveSmallIntegerField(choices=ROLE_CHOICES, primary_key=True)    def __str__(self):        return self.get_id_display()