from hospital.models import Doctorfrom hospital.serializers.doctor_serializers import DoctorSerializerfrom hospital.permissions.permissions import *from rest_framework.viewsets import ModelViewSetfrom rest_framework.pagination import PageNumberPaginationclass StandardResultsSetPagination(PageNumberPagination):    page_size = 8    page_size_query_param = 'page_size'    max_page_size = 10class DoctorViewSet(ModelViewSet, PermissionHelperMixin):    queryset = Doctor.objects.all()    serializer_class = DoctorSerializer    pagination_class = StandardResultsSetPagination    def perform_create(self, serializer):        return serializer.save(user=self.request.user)    def perform_update(self, serializer):        return serializer.save(user=self.request.user)    def get_permissions(self):        return self.authenticated_user_editable_only()