from hospital.models import Doctorfrom hospital.serializers.doctor_serializers import DoctorSerializerfrom hospital.permissions.permissions import *from rest_framework.viewsets import ModelViewSetclass DoctorViewSet(ModelViewSet, PermissionHelperMixin):    queryset = Doctor.objects.all()    serializer_class = DoctorSerializer    def perform_create(self, serializer):        return serializer.save(user=self.request.user)    def perform_update(self, serializer):        return serializer.save(user=self.request.user)    def get_permissions(self):        return self.admin_editable_only()