from hospital.models.management import *from hospital.serializers.management_serializer import *from hospital.helpers import *from rest_framework.viewsets import ModelViewSetclass ExperienceViewSet(ModelViewSet):    queryset = Experience.objects.all()    serializer_class = ExperienceSerializerclass AvailabilityViewSet(ModelViewSet):    queryset = Availability.objects.all()    serializer_class = AvailabilitySerializerclass LaboratoriesViewSet(ModelViewSet):    queryset = Laboratories.objects.all()    serializer_class = LaboratoriesSerializerclass DepartmentViewSet(ModelViewSet):    queryset = Department.objects.all()    serializer_class = DepartmentSerializerclass ServiceViewSet(ModelViewSet):    queryset = Service.objects.all()    serializer_class = ServiceSerializer